<script type="text/javascript">
	function createElementByString(str) {
		let div = document.createElement('div');
		div.innerHTML = str;

		return div.childNodes;
	}

	function initFollow(elem) {
		let icons = elem.querySelectorAll('[class*=" icon-"]', 'a');

		if (icons.length === 0) {
			return false;
		}

		for (let i = 0; i < icons.length; i++) {
			let icon = icons[i];
			let elem = createElementByString('<div class="follow-qrcode"><div class="qrcode"></div></div>')[0];
			let qrcode = elem.querySelector('.qrcode');
			qrcode.innerHTML = '<img alt="Scan me!" src="' + icon.dataset.src + '" />';
			icon.appendChild(elem);
		}
	}

	initFollow($("#follow")[0]);

	$("#follow_show")[0].onclick = function() {
		$("#follow_container").addClass("opened");
	};
</script>